    def reveal_prime(self):
        state=self.lineEdit_2.echoMode()
        if(state==2):
            self.lineEdit.setEchoMode(QtWidgets.QLineEdit.Normal)
            self.lineEdit_2.setEchoMode(QtWidgets.QLineEdit.Normal)
        else:
            self.lineEdit.setEchoMode(QtWidgets.QLineEdit.Password)
            self.lineEdit_2.setEchoMode(QtWidgets.QLineEdit.Password)
    def generate_key(self):
        p1=int(self.lineEdit_2.text())
        p2=int(self.lineEdit.text())
        self.priv,self.public=RSA_Functions.generate_key(p1,p2)
        self.textBrowser.clear()
        self.textBrowser.append("{},{}".format(str(self.public[0]),str(self.public[1])))
        self.textBrowser_2.clear()
        self.textBrowser_2.append("{},{}".format('X'*len(str(self.priv[0])),'X'*len(str(self.priv[1]))))
        self.t1=0
        self.textBrowser_3.clear()
        self.textBrowser_5.clear()
        self.textBrowser_5.append("Key Generated")
        self.textBrowser_3.append("Key Generated")
        
        return self.priv,self.public
    def reveal_priv(self):
        self.textBrowser_2.clear()
        if(self.t1&1==0):
            self.textBrowser_2.append("{},{}".format(str(self.priv[0]),str(self.priv[1])))
            self.t1+=1
        else:
            self.textBrowser_2.append("{},{}".format('X'*len(str(self.priv[0])),'X'*len(str(self.priv[1]))))
            self.t1+=1
    def get_text_input(self):
        self.text_input=self.PlainTextEdit.toPlainText()
    def dis_text(self):
        self.textBrowser_4.clear()
        self.textBrowser_4.append(self.text_input)
    def get_file(self):
        self.image_input,_=QtWidgets.QFileDialog.getOpenFileName(self.tab,'Get Image',r"C:\\Users\\harsh\Documents\\GitHub\\RSA-Cryptic-Technique","Image (*.jfif *.png *.jpeg *.jpg *.bmp)")
        self.label_11.setPixmap(QtGui.QPixmap(self.image_input))
    def get_input_file(self):
        self.text_input,_=QtWidgets.QFileDialog.getOpenFileName(self.tab,'Get Text',r"C:\\Users\\harsh\Documents\\GitHub\\RSA-Cryptic-Technique","Text File (*.txt)")
        self.text_input=RSA_Functions.readfile(self.text_input)
    def load_original_image(self):
        self.label_11.setPixmap(QtGui.QPixmap(self.image_input))
    def load_output(self):
        self.label_11.setPixmap(QtGui.QPixmap("C:\\Users\\harsh\Documents\\GitHub\\RSA-Cryptic-Technique\\output.jpg"))
    def encrypt_image(self):
        self.textBrowser_5.clear()
        self.textBrowser_5.append("Encrypting...")
        QtWidgets.QApplication.processEvents()
        time.sleep(0.2)
        img=RSA_Functions.read_data(self.image_input,1)
        self.image_output=RSA_Functions.cryptanalyse_image(img,self.public)
        RSA_Functions.writeimg('output',self.image_output.astype(np.uint8))
        self.textBrowser_5.clear()
        self.textBrowser_5.append("Encrypted!!")
        QtWidgets.QApplication.processEvents()
    def encrypt_text(self):
        self.textBrowser_3.clear()
        self.textBrowser_3.append("Encrypting...")
        QtWidgets.QApplication.processEvents()
        time.sleep(0.2)
        self.text_output=RSA_Functions.cryptanalyse_text(RSA_Functions.process(self.text_input),self.public)
        self.textBrowser_3.clear()
        self.textBrowser_3.append("Encrypted!!")
        QtWidgets.QApplication.processEvents()
    def decrypt_text(self):
        self.textBrowser_3.clear()
        self.textBrowser_3.append("Decrypting...")
        QtWidgets.QApplication.processEvents()
        time.sleep(0.2)
        self.text_output=RSA_Functions.cryptanalyse_text(self.text_output,self.priv)
        self.textBrowser_3.clear()
        self.textBrowser_3.append("Decrypted!!")
        QtWidgets.QApplication.processEvents()
    def dis_out_text(self):
        self.textBrowser_4.clear()
        self.textBrowser_4.append(RSA_Functions.reprocess(self.text_output))

    def decrypt_image(self):
        self.textBrowser_5.clear()
